          , template = '<html xmlns:o="urn:schemas-microsoft-com:office:office"                xmlns:x="urn:schemas-microsoft-com:office:excel"        xmlns="http://www.w3.org/TR/REC-html40"><head>                <!--[if gte mso 9]><xml><x:ExcelWorkbook><x:ExcelWorksheets>                <x:ExcelWorksheet><x:Name>{worksheet}</x:Name>                <x:WorksheetOptions><x:DisplayGridlines/></x:WorksheetOptions>                </x:ExcelWorksheet></x:ExcelWorksheets></x:ExcelWorkbook>                </xml><![endif]--></head><body>                <table border=1>{table}</table></body></html>'                , base64 = function (s) { return window.btoa(unescape(encodeURIComponent(s))) }                , format = function (s, c) { return s.replace(/{(\w+)}/g, function (m, p) { return c[p]; }) }                return function (table, name) {                if (!table.nodeType) table = document.getElementById(table)                var ctx = { worksheet: name || 'Worksheet', table: table.innerHTML }                window.location.href = uri + base64(format(template, ctx))            }        })()        function spanRow(column) {    var categoryColumn = $("table tr td:nth-child(" + column +")");    var rowspan = 1;    var lastElement = categoryColumn.first();    categoryColumn.each(function () {        var element = $(this);        if ($.trim(element.html()) == '') {            element.remove();            rowspan++;        } else {            lastElement.attr("rowspan", rowspan);            lastElement = element;            rowspan = 1;        }    });    lastElement.attr("rowspan", rowspan);}spanRow(2);spanRow(1);    </script>      </div>      <!-- *****        **** Main container end ***** Main container end ****        ***** -->